package com.authentication.cdlauthentication.domain.model;import com.fasterxml.jackson.annotation.JsonIgnore;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.authority.SimpleGrantedAuthority;import org.springframework.security.core.userdetails.UserDetails;import javax.persistence.*;import java.util.Collection;import java.util.Collections;import java.util.Set;@Entity@Table(name = "user")public class AppUser implements UserDetails {    @Id    @GeneratedValue(strategy = GenerationType.AUTO)    private Long id;    private String username;    @JsonIgnore    private String password;    @ManyToMany    private Set<Role> role;    public AppUser() {}    public String getUsername() {        return username;    }    @Override    public boolean isAccountNonExpired() {        return true;    }    @Override    public boolean isAccountNonLocked() {        return true;    }    @Override    public boolean isCredentialsNonExpired() {        return true;    }    @Override    public boolean isEnabled() {        return true;    }    public void setUsername(String username) {        this.username = username;    }    @Override    public Collection<? extends GrantedAuthority> getAuthorities() {        return Collections.singleton(new SimpleGrantedAuthority("USER"));    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public Set<Role> getRole() {        return role;    }    public void setRole(Set<Role> role) {        this.role = role;    }    public AppUser(String username, String password, Set<Role> role) {        this.username = username;        this.password = password;        this.role = role;    }}