package com.authentication.cdlauthentication.domain.repository;import com.authentication.cdlauthentication.domain.model.AppUser;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.stereotype.Repository;import java.util.Optional;@Repositorypublic interface AppUserRepository extends JpaRepository<AppUser,Long> {    Optional<AppUser> findUserByUsername(String username);    boolean existsByUsername(String username);}