package com.authentication.cdlauthentication.service.userDetailsService;import com.authentication.cdlauthentication.domain.repository.AppUserRepository;import lombok.RequiredArgsConstructor;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@Service@RequiredArgsConstructorpublic class CustomUserDetailsService implements UserDetailsService {    private final AppUserRepository appUserRepository;    @Override    public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {        return appUserRepository.findUserByUsername(username).orElseThrow(()->new NullPointerException("Not user found"));    }}