package com.authentication.loanProduction.api;import com.authentication.loanProduction.domain.model.ProductInformation;import com.authentication.loanProduction.service.ProductInformationService;import lombok.AllArgsConstructor;import lombok.NoArgsConstructor;import lombok.extern.slf4j.Slf4j;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/v1/product")@AllArgsConstructor@NoArgsConstructor@Slf4jpublic class ProductInformationController {    @Autowired    private ProductInformationService productInformationService;    @GetMapping("/information")    public ResponseEntity<List<ProductInformation>> productInformation(){        List<ProductInformation> productInformation = productInformationService.generateInfo();        log.info("controller result {}", productInformation);        return new ResponseEntity<>(productInformation, HttpStatus.OK);    }}