package com.authentication.loanProduction.service;import com.authentication.loanProduction.domain.model.ProductInformation;import com.authentication.loanProduction.domain.repository.ProductInformationRepo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class ProductInformationImpl implements ProductInformationService{    @Autowired    private ProductInformationRepo productInformationRepo;    @Override    public List<ProductInformation> generateInfo() {        return productInformationRepo.findAll();    }}