package com.cdl.basicauth.service;import com.cdl.basicauth.data.repo.AppUserRepository;import lombok.RequiredArgsConstructor;import org.springframework.security.core.userdetails.UserDetails;import org.springframework.security.core.userdetails.UserDetailsService;import org.springframework.security.core.userdetails.UsernameNotFoundException;import org.springframework.stereotype.Service;@Service@RequiredArgsConstructorpublic class CustomeUserDetailsService implements UserDetailsService {    private final AppUserRepository appUserRepository;    @Override    public UserDetails loadUserByUsername(String username) throws UsernameNotFoundException {        return appUserRepository.findUserByUsername(username);    }}